INSERT INTO public.book (library_id, author, avaible, description, editor, gender, parution, picture, title) VALUES ( '1', 'J.R.R. TOLKIEN', true, 'Dans les vertes prairies du Comté, les Hobbits, ou Demi-hommes, vivaient en paix... Jusqu''au jour fatal où l''un d''entre eux, au cours de ses voyages, entra en possession de l''Anneau Unique aux immenses pouvoirs. Pour le reconquérir, Sauron, le seigneur Sombre, va déchaîner toutes les forces du Mal. Frodon, le Porteur de l''Anneau, Gandalf, le magicien, et leurs intrépides compagnons réussiront-ils à écarter la menace qui pèse sur la Terre du Milieu ?', 'Pocket', 'Fantasy', '2017-08-06 02:00:00.000000', '../assets/img/tome1SeigneurDesAnneaux.jpg', 'Le Seigneur des anneaux - tome 1 : La fraternité de l''anneau');
INSERT INTO public.book (library_id, author, avaible, description, editor, gender, parution, picture, title) VALUES ( '2', 'J.R.R. TOLKIEN', false, 'Dans les vertes prairies du Comté, les Hobbits, ou Demi-hommes, vivaient en paix... Jusqu''au jour fatal où l''un d''entre eux, au cours de ses voyages, entra en possession de l''Anneau Unique aux immenses pouvoirs. Pour le reconquérir, Sauron, le seigneur Sombre, va déchaîner toutes les forces du Mal. Frodon, le Porteur de l''Anneau, Gandalf, le magicien, et leurs intrépides compagnons réussiront-ils à écarter la menace qui pèse sur la Terre du Milieu ?', 'Pocket', 'Fantasy', '2017-08-06 02:00:00.000000', '../assets/img/tome1SeigneurDesAnneaux.jpg', 'Le Seigneur des anneaux - tome 1 : La fraternité de l''anneau');
INSERT INTO public.book (library_id, author, avaible, description, editor, gender, parution, picture, title) VALUES ( '1', 'J.R.R. TOLKIEN', true, 'Les neufs compagnons avaient fondé la Fraternité de l''Anneau, tous soudés autour de Frodon, dépositaire de l''Anneau de Pouvoir, chargé de le détruire en le jetant dans la Montagne du Feu, où il fut forgé par son maître. Mais c''est là une quête terrible et semée d''embûches. Leur guide, Gandalf, a disparu, Boromir, comme pris de folie, a voulu s''emparer de l''Anneau, Frodon a dû s''échapper et poursuit, esseulé, mais déterminé, son voyage jusqu''au cœur du Mordor. La Fraternité est dissoute, les Ténèbres s''étendent toujours plus, et l''avenir de la Terre du Millieu repose sur les frêles épaules d''un courageux petit Hobbit.', 'Pocket', 'Fantasy', '2017-09-02 02:00:00.000000', '../assets/img/tome2SeigneurDesAnneaux.jpg', 'Le Seigneur des anneaux - tome 2 : Les Deux Tours');
